import React, { Component, Fragment } from 'react';
import axios from 'axios';
import Link from 'next/link'
class SimLogin extends Component {
    constructor(props) {
        super(props)

        this.state = {
            valid: true,
            email: '',
            password: '',
            registrationErrors: ''
        }

        this.handleChange = this.handleChange.bind(this)
        this.handleSumbit = this.handleSumbit.bind(this)
    }

    handleChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleSumbit(e) {
        e.preventDefault();
        const {
            email,
            password,
        } = this.state;

        axios.post('http://localhost:3000/api/hello', {
            user: {
                email: this.state.email,
                password: this.state.password,

            }
        },
        )
            .then(Response => {
                console.log("post realiza", Response)
            })
            .catch(error => {
                console.log("ingreso error", error)
            });
    }

    render() {
        return (
            <Fragment>
                <div className="formLogin">
                    <form onSubmit={this.handleSumbit}>
                        <h1>Ingreso</h1>
                        <p>Email *</p>
                        <input className="inputIngreso"
                            autoComplete="off"
                            type="email"
                            name="email"
                            value={this.state.email}
                            onChange={this.handleChange}
                        />
                        <p>Contraseña *</p>
                        <input className="inputIngreso"
                            autoComplete="off"
                            type="password"
                            name="password"
                            value={this.state.password}
                            onChange={this.handleChange}
                        />
                        <a href="/empty" type="submit"><p className="recContr"> Recuperar contraseña</p></a>
                        <a href="index"><button disabled={this.valid} className="btnPrimario" type="submit"> Ingresar</button></a>
                    </form><br />
                    <a href="http://localhost:3000/registro" target="_blank"><button className="btnSecundario">Registrarse</button></a>
                </div>
            </Fragment>

        )
    }

}

export default SimLogin;
